function logar(){let o=document.forms.formLogin.inputUsuario.value,e=document.forms.formLogin.inputSenha.value,a=$("#formLogin").serialize();""!=o&&""!=e?$.ajax({url:diretorio+"/login/sessao",type:"post",data:a,error:function(o){console.log(o),mensagem("danger","Erro ao logar, tente novamente! Por favor, verifique.")},success:function(o){switch(o.trim()){case"campo":mensagem("warning","Campos obrigatórios não foram preenchidos! Por favor, verifique.");break;case"senha":mensagem("warning","Usuário e/ou senha inválidos. Por favor, verifique.");break;case"erro":mensagem("danger","Erro ao logar, tente novamente.");break;case"logado":window.location.href=diretorio+"/";break;default:console.log(o),mensagem("danger","Falha ao logar. Contate o Administrador do Sistema.")}}}):mensagem("warning","Campos obrigatórios não foram preenchidos! Por favor, verifique.")}$("input").keyup(function(o){13==o.keyCode&&logar()});